class CardContainer extends React.Component {
 constructor(props) {
 super(props);
 this.state = {
 cards: []
 };
 }

 componentDidMount() {
 fetch('cards.json')
 .then(res => res.json())
 .then((result) => {
 this.setState({
 cards: result
 });
 });
 }

 render() {
 const cards = this.state.cards;
 let items = cards.map(
 card => <Card key={card.id} {...card} />
 );
 return (
 <div className='container pt-4'>
 <h3 className='text-center text-primary'>Products</h3>
 <div className="pt-4 row">
 {items}
 </div>
 </div>
 );
 }
}
